puncthe_c.lm <- lm(formula = Punctate_Thenar_c ~ Left_Cerebellum_Cortex + Right_Cerebellum_Cortex + Left_Cerebellum_White_Matter + Right_Cerebellum_White_Matter + EstimatedTotalIntraCranialVol + Chronological_Age + EHI_Score + CES_D_Score + Gender, data = MLpain_old_vol_outlier)
